mapper.insert:
INSERT INTO items 
  (name
  ,image
  ,description
  ,price
  ,type
  ,status
  , created_at, updated_at)
VALUES
  (#{name}
  ,#{image}
  ,#{description}
  ,#{price}
  ,#{type}
  ,#{status}
  , #{createdAt}
  ,#{updatedAt})

mapper.select:
SELECT COUNT(1) > 0
FROM items
WHERE
  name = #{name}
  AND type = #{type}

mapper.update:
UPDATE items i
SET
  i.name = #{name}
  ,i.description = #{description}
  ,i.price = #{price}
  ,i.type = #{type}
  ,i.image = #{image}
  ,status = #{status}
  ,i.updated_at = #{updatedAt}
WHERE i.id = #{id}

UPDATE items i
SET
  i.status = #{status}
WHERE i.id = #{id}
